import React, { Component } from 'react';

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nameFilm: '',
      url: '',
      comment: '',
    };
    this.onChangeNameFilm = this.onChangeNameFilm.bind(this);
    this.onChangeUrl = this.onChangeUrl.bind(this);
    this.onChangeComment = this.onChangeComment.bind(this);
  }



  onChangeNameFilm = (e) => {
    this.setstate({ nameFilm : e.target.value });
  };
  onChangeUrl = (e) => {
    this.state({ url: e.target.value });
  };
  onChangeComment = (e) => {
    this.state({ comment: e.target.value });
  };

  render() {
    return <div className="Formulaire">
        <form>
        <h1>Favourite Film</h1>
        <fieldset>
        <legend>Information</legend>
            <div className="form-data">
              <label htmlFor="nameFilm">Name Film :</label>
              <input
                type="text"
                id="nameFilm"
                name="nameFilm"
                onChange={this.onChangeNameFilm}
                value={this.state.nameFilm}
              />
            </div>
            <div className="form-data">
              <label htmlFor="url">URL :</label>
              <input
                type="text"
                id="url"
                name="url"
                onChange={this.onChangeUrl}
                value={this.state.url}
              />
            </div>
            <div className="form-data">
              <label htmlFor="comment">Comment :</label>
              <input
                type="text"
                id="comment"
                name="comment"
                onChange={this.onChangeComment}
                value={this.state.comment}
              />
            </div>
        </fieldset>
        </form>
    </div>;
  }
}
export default Form;
